document.addEventListener('DOMContentLoaded', function() {
    var input = document.querySelector('.datepicker-input');
    var calendar = document.createElement('div');
    calendar.className = 'datepicker-calendar';
    calendar.innerHTML = '<p>Custom Date Picker Calendar (not functional)</p>';
    document.body.appendChild(calendar);

    input.addEventListener('focus', function() {
        calendar.style.display = 'block';
        var rect = input.getBoundingClientRect();
        calendar.style.left = rect.left + 'px';
        calendar.style.top = (rect.bottom + window.scrollY) + 'px';
    });

    document.addEventListener('click', function(event) {
        if (!input.contains(event.target) && !calendar.contains(event.target)) {
            calendar.style.display = 'none';
        }
    });
});
